mapper.select:
SELECT HSH.ORDER_NUMBER AS H_ORDER_NUMBER ,HCO.COMPANY_NAME AS H_COMPANY_NAME ,HCU.CUSTOMER_NAME AS H_CUSTOMER_NAME ,HSH.ORDER_DATE AS H_ORDER_DATE ,HSH.ORDER_STATUS AS H_ORDER_STATUS ,SUM(HSL.ORDER_QUANTITY * HSL.UNIT_SELLING_PRICE) AS H_ORDER_AMOUNT FROM HODR_SO_HEADER_5224 HSH LEFT JOIN HODR_SO_LINE_5224 HSL ON HSH.SO_HEADER_ID = HSL.SO_HEADER_ID ,HODR_COMPANY_5224 HCO ,HODR_CUSTOMER_5224 HCU WHERE HSH.COMPANY_ID = HCO.COMPANY_ID AND HSH.COMPANY_ID = HCO.COMPANY_ID AND HSH.CUSTOMER_ID = HCU.CUSTOMER_ID
AND HSH.COMPANY_ID = #{companyId}
AND HSH.CUSTOMER_ID = #{customerId}
AND UPPER(HSH.ORDER_NUMBER) LIKE UPPER(CONCAT('%',#{orderNumber},'%'))
AND HSH.ORDER_STATUS = #{orderStatus}
GROUP BY HSH.ORDER_NUMBER ,HCO.COMPANY_NAME ,HCU.CUSTOMER_NAME ,HSH.ORDER_DATE ,HSH.ORDER_STATUS ORDER BY HSH.ORDER_DATE ,HSH.ORDER_NUMBER
